    private static int rangeSum(int[] nums, int n, int left, int right) {
        ArrayList<Integer> subArray = new ArrayList<>();
        for (int i : nums) {
            subArray.add(i);
        }
        for (int i = 0; i < n; i++) {
            int num = nums[i];
            for (int j = i + 1; j < n; j++) {
                num += nums[j];
                subArray.add(num);
            }
        }
        Collections.sort(subArray);
        int sum = 0;
        for (int i = left - 1; i < right && i < subArray.size(); i++) {
            sum += subArray.get(i);
            sum %= (int) (1e9 + 7.);
        }
        return sum;
    }
